CREATE TABLE `apply_award` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `supply_employee_uuid` varchar(32) NOT NULL COMMENT '申请人的uuid',
  `student_id` int(11) NOT NULL COMMENT ' 给谁申请的',
  `expect_event_task_id` int(11) NOT NULL COMMENT '对应的task_id',
  `amount` int(11) NOT NULL COMMENT ' 申请的金额，单位是分',
  `reissue_reason` varchar(255) NOT NULL COMMENT '补发原因',
  `image_key` json DEFAULT NULL COMMENT '附图',
  `create_time` int(11) NOT NULL COMMENT '发起审批时间',
  `status` tinyint(1) NOT NULL COMMENT '审核状态 1发起审批 2审批拒绝 3审批通过 4审批撤销',
  `workflow_id` varchar(125) NOT NULL COMMENT '工作流实例id',
  `update_time` int(11) NOT NULL COMMENT '更新时间',
  `cash_award_id` int(11) DEFAULT NULL COMMENT '奖励表的id',
  PRIMARY KEY (`id`)
) COMMENT '申请奖励数据表';

