CREATE TABLE `country_code` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `name` VARCHAR(64) NULL COMMENT '中文名称',
  `en_name` VARCHAR(64) NOT NULL COMMENT '国际名称',
  `en_code` VARCHAR(10) NULL COMMENT '国际简码',
  `country_code` VARCHAR(10) NOT NULL COMMENT '国家地区代码',
  `sms_code` VARCHAR(10) NOT NULL COMMENT '短信国际代码',
  `sms_price` FLOAT NOT NULL COMMENT '短信价格',
  `status` TINYINT(2) NOT NULL DEFAULT 1 COMMENT '是否启用',
  PRIMARY KEY (`id`),
  INDEX `country_code` (`country_code` ASC))
  COMMENT = '国家编码';

